    private void Fim(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Fim
        if(null == A.getText()) {
            PV = true;
            A.setText("");
            Operacoes O = new Operacoes(Operacoes);
            float resultado = O.main();
            boolean verifica = O.getvalidade();
            if (verifica) {
                String R = Float.toString(resultado);
                A.append(R);
            } else {
                A.append("Erro");
            }
            Operacoes = "";
        } else switch (A.getText()) {
            case "Erro":
                A.setText("");
                break;
            case "":
                A.setText("");
                break;
            default:
                PV = true;
                A.setText("");
                Operacoes O = new Operacoes(Operacoes);
                float resultado = O.main();
                boolean verifica = O.getvalidade();
                if (verifica) {
                    String R = Float.toString(resultado);
                    A.append(R);
                } else {
                    A.append("Erro");
                }   Operacoes = "";
                break;
        }
    }//GEN-LAST:event_Fim

