    private void BDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BDeleteActionPerformed
        if (Operacoes != null || !"".equals(Operacoes)) {
            String aux = "";
            String Opf = "";
            char[] Op_char = Operacoes.toCharArray();
            int i = 0;
            A.setText("");
            while (i < Op_char.length - 1) {
                if(Op_char[i] == '+'
                        || Op_char[i] == '-'
                        || Op_char[i] == '*'
                        || Op_char[i] == '/'
                        || Op_char[i] == '^'){
                    A.append(" ");
                    aux += Op_char[i];
                    A.append(aux);
                    aux = "";
                    A.append(" ");
                }else if(Op_char[i] == 'r'){
                    aux += "²√";
                    A.append(aux);
                    aux = "";
                }else if(Op_char[i] == '!'){
                    aux += "!";
                    A.append(aux);
                    aux = "";
                }else if(Op_char[i] == 'l'){
                    aux += "ln";
                    A.append(aux);
                    aux = "";
                }else if(Op_char[i] == 'L'){
                    aux += "log";
                    A.append(aux);
                    aux = "";
                }else if(Op_char[i] == 'p'){
                    aux += "%";
                    A.append(aux);
                    aux = "";
                }else if(Op_char[i] == 'R'){
                    aux += "√";
                    A.append(aux);
                    aux = "";
                }else{
                    aux += Op_char[i];
                    A.append(aux);
                    aux = "";
                }
                Opf += Op_char[i];
                i++;
            }
            Operacoes = Opf;
            
        }
    }//GEN-LAST:event_BDeleteActionPerformed

